import axios from 'axios';
import Cards from '../Cards/Cards';
import "./Deck.css";

function rand(){
    return Math.floor(Math.random() *360+1);
}
const Deck = () =>{
   const[id,setId] = useState("")
   const[cards,setCards] = useState([])
   const[success,setSuccess] = useState(true)
   const[rotate,setRotate] = useState([])
   const[click, setClick] = useState({})

   
   /* constructor(props) {
       super(props);
       this.state={id : "",cards:[],success:"true",rotate:[]};
       this.handleClick = this.handleClick.bind(this);*/
       
       const handleClick = async (event) => {
           let card = await axios.get(`https://deckofcardsapi.com/api/deck/${id}/draw/?count=5`)
           /*setClick({cards:[...cards, ...card.data.cards],rotate:[...rotate,rand()],success : card.data.success})*/
           setCards(...cards, ...card.data.cards)
           
        }
        useEffect(() => {
            async function getData() {
                let res = await axios.get('https://deckofcardsapi.com/api/deck/new/shuffle')
                let data = res.data;
                setId(data.deck_id)
            }
            getData()
        },[]) 
        
        
        if(!success){
            alert('Deck Get Empty !!!!!!!!!')
        }
        console.log(cards)
        return( 
            <div className="Deck">
        <h1 className='Deck-title'>ðŸ”¶Deck Of CardsðŸ”¶</h1>
        <button onClick={handleClick} className='Deck-btn'>Get Card!!</button>
        <div className='Deck-cardarea'>
        {cards.map((m,idx)=> <Cards src={m.image} key={idx} alt={m.code} rotate={rotate[idx]}/>)}
            </div>
     

    </div>
)
}


export default memo(Deck)
